var askConfirm, getVideoFolder, languages, openVideoFolder, platform, resolutions, serverReply, showAlert, socket, socket_send,
  indexOf = [].indexOf;

socket = null;

platform = location.hash.split(',')[1];

serverReply = '';

(function() {
  var msg, port, ref;
  switch (location.hash) {
    case '#BUSY':
      document.body.innerHTML = 'WebSocket already in use';
      throw new Error("Stop execution: socket already in use");
    case '#ERROR':
      document.body.innerHTML = 'WebSocket connection error';
      throw new Error("Stop execution: socket connection error");
  }
  port = location.hash.split(',')[2];
  if (ref = parseInt('0' + port), indexOf.call([8080, 8081, 8082, 8083, 8084, 8085, 8086, 8087, 8088, 8089], ref) < 0) {
    msg = 'This web application must be started by the WebSocket server.';
    document.body.innerHTML = msg;
    throw new Error("Stop execution: trying to launch browser directly");
  }
  socket = new WebSocket(`ws://localhost:${port}/ws`);
  socket.onopen = function(event) {
    return console.log('WebSocket connection established');
  };
  socket.onclose = function(event) {
    console.log('WebSocket connection closed');
    return document.body.innerHTML = 'Application closed.';
  };
  return socket.onmessage = function(event) {
    // Received messages from server
    return serverReply = event.data;
  };
})();

// --------------------------------------
socket_send = function(action, command) {
  var cmd;
  cmd = JSON.stringify({
    id: location.hash,
    action: action,
    cmd: command
  });
  return socket.send(cmd);
};

// ******************* END OF WEBSOCKET HANDLING ***********************
window.addEventListener('beforeunload', function(event) {
  // Terminate the external socket server program
  return socket_send('exit', '');
});

